INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Pugsley\'s Signature Series XXXX IPA', 5424, 1136, 3, 32, 9.25, 'XXXX IPA is a non-traditional American IPA with a brilliant copper color and the classic citrus nose of Cascade hops. This beer demonstrates a unique balance of malt-inspired, delicate red grapefruit sweetness and lingering hop dryness. The OG and final ABV provide the structure and body to balance the harmony of distinct flavours. Cascade, Warrior, Summit and Glacier Hops are used for bittering and Cascade Hops are added for dry hopping after fermentation. This hop blend is well balanced with Malted Wheat, Pale Ale, Crystal, and Caramalt Malts. To fully enjoy all the flavours, this ale is best drunk at 55 degrees Fahrenheit. This beer pairs well with Cajun dishes, blackened fish, and BBQ. XXXX draws its name from the British brewing convention of using Xâ€™s to denote style. 70 BUâ€™s, 1.092 OG, 9.25% ABV.', 'Imperial or Double India Pale Ale', 'Shipyard Brewing - Portland', '86 Newbury Street', 'Portland', 'Maine', 'United States', '', '43.6619, -70.2489', 'http://www.shipyard.com/');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Vivant Tripel', 5894, 1416, 5, 61, 9.25, 'A classic golden ale, with subtle esters of banana and bubblegum. This beer pours with a creamy head and ends with a sweetness of light Belgian candi sugars. Our brewmaster aged this ale for an extended period to round out the flavors. A great traditional Belgian brew.', 'Belgian-Style Tripel', 'Brewery Vivant', '925 Cherry Street SE', 'Grand Rapids', 'MI', 'United States', '', '', 'http://breweryvivant.com');
