INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Scratch #8 2008', 4648, 1286, 5, 61, 10.100000381, 'Revisiting Scratch #3, we take the same Tripel recipe, add beet and cane sugars to the kettle, and run the wort through our hopback brimming with juniper berries, coriander and yarrow flowers.\n\n\nInspired by the herbaceous nose of gin, the berries and flowers give a slight pine/citrus aroma with floral undertones. Scratch #8 has a dry, yeasty finish and noticeable alcohol warming when consumed.', 'Belgian-Style Tripel', 'Troegs Brewing', '800 Paxton Street', 'Harrisburg', 'Pennsylvania', 'United States', '', '40.2559, -76.8715', 'http://www.troegs.com/');
