INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Gran Met', 5328, 1330, 5, 65, 10.5, 'Our Gran Met (Grand Master) is an Ale made from our Propriatery yeast from a small brewery in Belgium. We use Cane and Beet sugar, along with German Pilsner Malt to create the unique flavor of this age old style. We also use a unique fermentation process of slowly adding the sugars to the fermentation during fermentation. This allows the yeast to more slowly ferment the beer without overloading the yeast. This process allows for a more clean, tight flavor profile.10% alc by vol. \n\n\nBottle Conditioned and Refermented.', 'Belgian-Style Pale Strong Ale', 'Voodoo Brewing Co.,LLC', '215 1/5 Arch St.', 'Meadville', 'Pennsylvania', 'United States', '', '41.6372, -80.1549', 'http://www.voodoobrewery.com/');
