INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Imperial Russian Stout', 5684, 1204, 3, 43, 10.5, 'Brewed in the authentic historical style of an Imperial Russian Stout, this ale is massive. Intensely aromatic (notes of anise, lack currants, coffee, roastiness and alcohol) and heavy on the palate, this brew goes where few can --- and fewer dare even try. The style originated from Czarist Russia\'s demand for ever thicker English stouts. Expect our version of this mysterious brew to pour like Siberian crude and taste even heavier!', 'American-Style Imperial Stout', 'Stone Brewing Co.', '1999 Citracado Parkway', 'Escondido', 'California', 'United States', '', '33.1157, -117.12', 'http://www.stonebrew.com/');
