INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Lust', 4833, 858, 5, 66, 11.5, 'LUST Belgian-style Dark Strong Ale stirs shameless desire in men (and women) with its captivating appearance, enticing aroma and satisfying flavors. Aged in bourbon oak barrels for twelve months, Lust is worldly, smooth and decadent. Sour cherries contribute tartness while brettanomyces brings muskiness to this naughty brew. \n\n\nThe pleasure you derive from this dark beer is beyond words. \n\n\nLUST. Regret. Repent. Repeat.', 'Belgian-Style Dark Strong Ale', 'Midnight Sun Brewing Co.', '8111 Dimond Hook Drive', 'Anchorage', 'Alaska', 'United States', '', '61.1473, -149.844', 'http://www.midnightsunbrewing.com/');
