INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Black Magick', 5331, 1330, 3, 43, 15, 'This is an Imperial Stout that is Brewed to great strength and complexity. We then gently age that beer in Elaigh Craig 13 1/2 year old Bourbon Barrels for 1 yr. and then primed and bottled to bottle condition for continued aging and celler life. Black Magick should be able to be aged up to about 5 years, to heighten the complexity and smooth nature of this beer. This beer should be opened to breath, pour into snifter and enjoy at 55-60 F. \n\n\nThis is a truly Barrel aged ale and is not to have a large carbonation. We bottle condition it to achieve cask like characteristics.', 'American-Style Imperial Stout', 'Voodoo Brewing Co.,LLC', '215 1/5 Arch St.', 'Meadville', 'Pennsylvania', 'United States', '', '41.6372, -80.1549', 'http://www.voodoobrewery.com/');
