INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Tactical Nuclear Penguin', 5713, 241, 3, 43, 32, 'This is the worlds strongest ever beer, ever (yes ever).\n\n\nNo Penguins  were harmed in the making of this beer; some humans did get very, very cold though. It was worth it.\n\n\nThe Antarctic name, inducing schizophrenia, of this Å¸ber-imperial stout originates from the amount of time it spent exposed to extreme cold. This beer was initially double barrel aged for 14 months; maturing in the deep, rich oak of Scottish whisky casks. After this epic maturation the beer was then frozen, then frozen again, then frozen again.\n\n\nThis is an extremely strong beer, it should be enjoyed in small servings and with an air of aristocratic nonchalance. In exactly the same manner that you would enjoy a fine whisky, a Frank Zappa album or a visit from a friendly yet anxious ghost.', 'American-Style Imperial Stout', 'BrewDog Ltd', 'AB43 8UE', 'Fraserburgh', '', 'United Kingdom', '', '57.683, -2.003', 'http://brewdog.com/');
